import React, {useState} from 'react';
import './App.css';


import WebglAppSin from "./webglAppSin";


export default function App() {


  let [data, setData] = useState([0]);
  let [datapoints, setDatapoints] = useState(0);

  async function requestDevice() {
    let device = await navigator.usb.requestDevice({ filters: [{ vendorId: 0x0 }] });
    console.log(device.configuration);
    await device.open();
    await device.claimInterface(0);
    console.log("Interface claimed");

    while (1) {
      let result = await device.transferIn(2, 64);
      if (result.data) {

        console.log('received', result.data.getUimap(byte => toUint8(byte)));
      //  setData([...data, result.data.getUint16(0)]);
      }
    }
  }



  return (
    <div>
      <div>
        <button onClick={requestDevice}>request device</button>
        <button onClick={() => setData([...data, Math.floor(Math.random() * 10)])}>data +</button>
        <button onClick={() => setData([0])}>reset data</button>
      </div>
      <div style={{ width: "100%" }}>
        <WebglAppSin data={data}/>
      </div>
    </div>
  );
}
