import React, {useState} from 'react';
import './App.css';

import WebglAppSin from "./webglAppSin";

import Button from 'react-bootstrap/Button';

async function getData(USBDevice) {
  // Read (up to) 64 bytes of data on USB endpoint 2
  let result = await USBDevice.transferIn(2, 64);
  if (result.data == null) return null;
  let newData = result.data;
  // Parse the received data
  let parsedData = [];
  for (let i = 0; i < 64; i++) {
    let val = newData.getUint8(i);
    parsedData.push(val);
  }
  return parsedData;
}

export default function App() {
  let [data, setData] = useState([[0]]);
  let [USBDevice, setUSBDevice] = useState(null);

  async function connectDevice() {
    let device = await navigator.usb.requestDevice({ filters: [{ vendorId: 0x0 }] });
    await device.open();
    await device.claimInterface(0);
    setUSBDevice(device);
  }

  async function readData() {
      let newData = await getData(USBDevice);
      if (newData != null) {
        data.push(newData);
        setData(data);
        console.log('read', newData.length, 'datapoints');
      }
  }

  function startCapture() {
    let interval = setInterval(readData, 1);
  }

  return (
    <div className='container'>
      <h1>WebScope</h1>
        <button onClick={connectDevice}>request device</button>
        <button onClick={startCapture} disabled={USBDevice == null}>read data</button>
      <div style={{ width: "100%" }}>
         <WebglAppSin data={data}/>        
      </div>
    </div>
  );
}
