import React, { useEffect, useRef } from "react";

import { WebglPlot, WebglLine, ColorRGBA } from "webgl-plot";

let webglp;
let line;


export default function WebglAppSin({data }) {
  const canvasMain = useRef(null);

  useEffect(() => {
    if (canvasMain.current) {
      const devicePixelRatio = window.devicePixelRatio || 1;
      canvasMain.current.width =
        canvasMain.current.clientWidth * devicePixelRatio;
      canvasMain.current.height =
        canvasMain.current.clientHeight * devicePixelRatio;

      webglp = new WebglPlot(canvasMain.current);
      const numX = 1000;

      line = new WebglLine(new ColorRGBA(1, 0, 0, 1), numX);
      webglp.addLine(line);

      line.arrangeX();
    }
  }, []);

  useEffect(() => {
    let id = 0;
    let renderPlot = () => {

    let packetSize = data[0].length;
      for (let i = 0; i < line.numPoints; i++) {
        line.setY(i, data[i][Math.round(i * data.length/ line.numPoints)] / 64);
      }
      id = requestAnimationFrame(renderPlot);
      webglp.update();
    };
    id = requestAnimationFrame(renderPlot);

    return () => {
      renderPlot = () => {};
      cancelAnimationFrame(id);
    };
  }, [data]);

  const canvasStyle = {
    width: "100%",
    height: "70vh"
  };

  return (
    <div>
      <canvas style={canvasStyle} ref={canvasMain} />
      <div>data points: {data.length}</div>
    </div>
  );
}
