import React, {useState} from 'react';
import './App.css';

import WebglAppSin from "./webglAppSin";

import ChannelControl from './ChannelControl';

import Button from 'react-bootstrap/Button';
import { ColorRGBA } from 'webgl-plot';

import getDummyData from './dummyData';
import HorizontalControl from './HorizontalControl';

import HoverBox from './HoverBox';
import MeasurementBox from './MeasurementBox';

import Plot from './Plot.js';

let dummyData = getDummyData();


async function getData(USBDevice) {
  // Read (up to) 64 bytes of data on USB endpoint 2
 // let result = await USBDevice.isochronousTransferIn(2, Array(2).fill(1023));
 let start = performance.now();
  let result = await USBDevice.transferIn(1, 32768);

  let kb = result.data.byteLength / 1024;
  let time = (performance.now() - start) / 1000;

  let speed = kb / time;
  console.log(kb, 'kb');
  console.log(time, 's');
  console.log(speed, 'kB/s');

  console.log(result.data)

  if (result.data == null) {
    console.log("received null");
    return null;
    }
  //console.log('received', result.data.byteLength, 'B');
  let newData = result.data;
  // Parse the received data
  let parsedData = [];
  for (let i = 0; i < result.data.byteLength; i++) {
    let val = newData.getUint8(i);
    parsedData.push(val);
  }
  return parsedData;
}




export default function App() {
  let [data, setData] = useState([]);
  let [smallData, setSmallData] = useState([]);
  let [USBDevice, setUSBDevice] = useState(null);
  let [captured, setCaptured] = useState(0);
  let [start, setStart] = useState(0);
  let [interval, setInterval2] = useState(0);
  let [running, setRunning] = useState(false);

  async function connectDevice() {
    let device = await navigator.usb.requestDevice({ filters: [{ vendorId: 0x0 }] });
    await device.open();
    await device.claimInterface(0);
    setUSBDevice(device);

    console.log(device);
  }

  async function readData() {
      let newData = await getData(USBDevice);
      if (newData != null) {
        data.push(newData);
        setData(data);
       // console.log('read', newData.length, 'datapoints');
        captured += newData.length;
        setCaptured(captured);
    }
  }


  function readLoop() {
    setStart(performance.now());
    USBDevice.transferIn(1, 32768).then((result) => {


      if (result.data.byteLength != 0) {
      let newData = result.data;
      // Parse the received data
      let parsedData = [];
      for (let i = 0; i < result.data.byteLength; i++) {
        let val = newData.getUint8(i);
        parsedData.push(val);
      }

        data.push(parsedData);
        setData(data);
       // console.log(data.length * 32768 / 1024 / ((performance.now() - start) / 1000));
      //  console.log(data);



      if (smallData.length > 100) smallData.splice(0, 1);
      smallData.push(parsedData[0]);

      setSmallData(smallData);
    }
      if (running) readLoop();
    });
  }


  function startCapture() {
    running = true;
    setRunning(true);
    readLoop();
  }

  function startSingle() {
    readData();
  }


  let [clk, setClk] = useState(0);
  async function sendConfig() {
    function str2ab(str) {
      var buf = new ArrayBuffer(str.length); // 2 bytes for each char
      var bufView = new Uint8Array(buf);
      for (var i = 0, strLen = str.length; i < strLen; i++) {
        bufView[i] = str.charCodeAt(i);
      }
      return buf;
    }

    let config = {
      clk_div: clk
    };

    let str = JSON.stringify(config);
    console.log(str);

    let buf = str2ab(str);
    console.log(buf);
    let result = await USBDevice.transferOut(1, buf);

  }

  function clkChange(event) {
    let val = parseInt(event.target.value);
    console.log(event.target.value);
    if (!isNaN(val)) setClk(val);
    if (event.target.value == '') setClk(0);
  }


  function stop() {
    running = false;
    setRunning(false);

    clearInterval(interval);
    console.log('stopped', interval);
    console.log(data.length);
  }


  let [showPlot, setShowPlot] = useState(true);
  return (
    <div className="root">
      <div className="topbar">
      <span role="img" aria-label="dog">{USBDevice == null ? "❌" : "✅"} </span>

        <button onClick={connectDevice}>Request device</button>
        <button onClick={startCapture} disabled={USBDevice == null}>read data</button>
        <button onClick={startSingle} disabled={USBDevice == null}>read single</button>

        <button onClick={stop}>stop</button>
        <button onClick={sendConfig}>send config</button>
        <input type="text" value={clk} size="5" onChange={clkChange}/>

        <button onClick={() => {setShowPlot(!showPlot)}}>plot</button>


        {/* Speed: {data[0] != null && data.length * 32768 / 1024 / ((performance.now() - start) / 1000)} */}
        { Math.round(data.length * 32768 / 1024)} kB
        </div>
      <div className="main">
        <div className='plot'>
        <WebglAppSin data={smallData}/>
       {/* <Plot data={data}/> */}

        </div>
        <div className='side'>
          <ChannelControl number="1" color="#ffff0078" active="true"/>
          <ChannelControl number="2" color="#00800069" active="false"/>
          <ChannelControl number="3" color="#0000ff73" style={{opacity: 1}}/>
         <HorizontalControl/>

        </div>
      </div>
    </div>
  );
}
