import React, {useState} from 'react';
import './App.css';
import {useQuery} from 'react-query';

import WebglAppSin from "./webglAppSin";
import { Graph } from './Graph';

export async function getData(USBDevice) {
  let result = await USBDevice.transferIn(2, 64);
  let newData = result.data;
  let parsedData = [];
  for (let i = 0; i < 64; i++) {
    let val = newData.getUint8(i);
    parsedData.push(val);
  }
  console.log(parsedData);
  return parsedData;
}

export default function App() {


  let [data, setData] = useState([0]);
  let [USBDevice, setUSBDevice] = useState(null);

  async function connectDevice() {
    let device = await navigator.usb.requestDevice({ filters: [{ vendorId: 0x0 }] });
    console.log(device.configuration);
    await device.open();
    await device.claimInterface(0);
    console.log("Interface claimed");

    setUSBDevice(device);

    console.log("device", device);
    console.log("USBDevice", USBDevice);
  }


  async function readLoop() {
    //setInterval(readData, 1000);
    let newData = await getData(USBDevice);
    setData(data.concat(newData));
  }



  return (
    <div>
      <div>
        <button onClick={connectDevice}>request device</button>
        <button onClick={readLoop}>read data</button>
        <button onClick={() => setData([0])}>reset data</button>
      </div>
     {USBDevice && <Graph props={USBDevice}/>}
    </div>
  );
}
