import React, {useState} from 'react';
import './App.css';


import WebglAppSin from "./webglAppSin";


export default function App() {


  let [data, setData] = useState([0]);
  let [datapoints, setDatapoints] = useState(0);
  let [USBDevice, setUSBDevice] = useState(null);

  async function connectDevice() {
    let device = await navigator.usb.requestDevice({ filters: [{ vendorId: 0x0 }] });
    console.log(device.configuration);
    await device.open();
    await device.claimInterface(0);
    console.log("Interface claimed");
    setUSBDevice(device);
  }
   async function readData() {

    for (let steps = 0; steps < 100; steps++) {
      let result = await USBDevice.transferIn(2, 64);
      if (result.data) {

        console.log('received', result.data);
        let parsedData = [];
        for (let i = 0; i < 64; i++) {
          let val = result.data.getUint8(i);
          console.log(val);
          parsedData.push(val);
        }
        console.log('parsed data', parsedData);
        setData(data.concat(parsedData));
        console.log('data', data);
      }
    }
  }





  return (
    <div>
      <div>
        <button onClick={connectDevice}>request device</button>
        <button onClick={readData}>read data</button>
        <button onClick={() => setData([...data, Math.floor(Math.random() * 10)])}>data +</button>
        <button onClick={() => setData([0])}>reset data</button>
      </div>
      <div style={{ width: "100%" }}>
        <WebglAppSin data={data}/>
      </div>
    </div>
  );
}
