import {useState} from 'react';
import './App.css';
import React from 'react';

import SimpleSlider from "simple-slider-react";

import WebglAppSin from "./webglAppSin";


function App() {
  const [counter, setCounter] = useState(0);


  let [freq, setFreq] = useState(0.001);
  let [amp, setAmp] = useState(0.5);

  const onUpdate = (value) => {
    setAmp(value / 100);
  };





  async function requestDevice() {
    let device = await navigator.usb.requestDevice({ filters: [{ vendorId: 0x0 }] });
    console.log(device.configuration);
    await device.open();
    await device.claimInterface(0);
    console.log("Interface claimed");

    device.transferIn(2, 64).then(result => console.log(result));
  }




  return (
    <div>
    <div>{counter} is {counter % 2 ? 'even' : 'odd'}</div>
    <button onClick={() => setCounter(counter + 1)}>+</button>
    <button onClick={() => setCounter(0)}>reset</button>
    <button onClick={requestDevice}>request device</button>

    <div className="mainapp" style={{ width: "90%" }}>
      <WebglAppSin freq={freq} amp={amp} />
      <SimpleSlider min={1} max={100} onDrag={onUpdate} onUpdate={onUpdate} />
    </div>


    </div>
  );
}

export default App;
