import React, {useState} from 'react';



function ValueControl(props) {
  return (
    <fieldset>
      <legend>{props.name}</legend>
<input value={props.value} size="2"/>{props.unit}
      <button>--</button><button>-</button><button>0</button><button>+</button><button>++</button>
    </fieldset>


  );
}

export default function ChannelControl({props, setState}) {
  return (
    <fieldset>
      <legend>
      Channel {props.channelNumber}       <button>Active</button> <br/>
      </legend>
      <ValueControl value={props.scale} name="Scale" unit="V"/>
    </fieldset>
  )
}
