import React, {useState} from 'react';
import './App.css';


import WebglAppSin from "./webglAppSin";


export default function App() {
  const [counter, setCounter] = useState(0);


  let [freq, setFreq] = useState(0.001);
  let [amp, setAmp] = useState(0.5);
  let [noise, setNoise] = useState(0.1);
  let [data, setData] = useState([0]);
  let [datapoints, setDatapoints] = useState(0);

  const onUpdate = (value) => {
    setAmp(value / 100);
  };



  async function requestDevice() {
    let device = await navigator.usb.requestDevice({ filters: [{ vendorId: 0x0 }] });
    console.log(device.configuration);
    await device.open();
    await device.claimInterface(0);
    console.log("Interface claimed");

    while (1) {
      await device.transferIn(2, 64).then(result => {
        setData([...data, 1, 2]); 
        console.log('rec')});
    }
  }



  return (
    <div>
      <div>
        <div>{counter} is {counter % 2 ? 'even' : 'odd'}</div>
        <button onClick={() => setCounter(counter + 1)}>+</button>
        <button onClick={() => setCounter(0)}>reset</button>
        <button onClick={requestDevice}>request device</button>
        <button onClick={() => setFreq(freq / 1.2)}>freq-</button>
        <button onClick={() => setFreq(freq * 1.2)}>freq+</button>
        <button onClick={() => setNoise(noise * 1.2)}>noise+</button>
        <button onClick={() => setData([...data, 1, 2, 3, 4, 5, 6, 7, 8, 9, 0])}>data+</button>
        <button onClick={() => setData([0])}>data0</button>
      </div>
      <div className="mainapp" style={{ width: "100%" }}>
        <WebglAppSin freq={freq} amp={amp} noise={noise} data={data}/>
      </div>
    </div>
  );
}
