import {useState} from 'react';
import './App.css';
import React from 'react';

function App() {
  const [counter, setCounter] = useState(0);

  async function requestDevice() {
    let device = await navigator.usb.requestDevice({ filters: [{ vendorId: 0x0 }] });
    console.log(device.configuration);
    await device.open();
    await device.claimInterface(0);
    console.log("Interface claimed");

    let msg = device.transferIn(2, 64).then(result => console.log(result));
  }




  return (
    <div>
    <div>{counter} is {counter % 2 ? 'even' : 'odd'}</div>
    <button onClick={() => setCounter(counter + 1)}>+</button>
    <button onClick={() => setCounter(0)}>reset</button>
    <button onClick={requestDevice}>request device</button>

    </div>
  );
}

export default App;
