import React, { useEffect, useRef } from "react";
import { getOverlayDirection } from "react-bootstrap/esm/helpers";

import { WebglPlot, WebglLine, ColorRGBA } from "webgl-plot";

import './App.css';


let webglp;
let line;
let line2;
let line3;


export default function WebglAppSin({data}) {
  const canvasMain = useRef(null);

  useEffect(() => {
    if (canvasMain.current) {
      console.log("webgl");
      const devicePixelRatio = window.devicePixelRatio || 1;
      canvasMain.current.width =
        canvasMain.current.clientWidth * devicePixelRatio;
      canvasMain.current.height =
        canvasMain.current.clientHeight * devicePixelRatio;

      webglp = new WebglPlot(canvasMain.current);
      const numX = 1000;

      line = new WebglLine(new ColorRGBA(0, 1, 0, 1), numX);
      webglp.addLine(line);
      line.arrangeX();

      line2 = new WebglLine(new ColorRGBA(1, 1, 0, 1), numX);
      webglp.addLine(line2  );
      line2.arrangeX();

      line3 = new WebglLine(new ColorRGBA(0, 0, 1, 1), numX);
      webglp.addLine(line3  );
      line3.arrangeX();
    }
  }, []);

  useEffect(() => {
    console.log('rendering frame');

    let flatData = data.length > 10 ? data.slice(data.length - 10, data.length).flat() : [1, 2, 3, 4];
   // let flatData = Array(data.length).fill().map(() => 256 * Math.random());

    let max = 256;
      for (let i = 0; i < line.numPoints; i++) {
        line.setY(i, 1.3 * flatData[Math.round(flatData.length * i / line.numPoints)] / max  - 0.55);
      }

    //  id = requestAnimationFrame(renderPlot);
      webglp.update();});



  return (
    <div className="webglDiv">
      <div>{data.length}</div>
      <canvas className="webgl" ref={canvasMain} />
    </div>
  );
}
