import React, {useEffect, useState} from 'react';
import './App.css';

import WebglAppSin from "./webglAppSin";

import ChannelControl from './ChannelControl';

import Button from 'react-bootstrap/Button';
import { ColorRGBA } from 'webgl-plot';

import getDummyData from './dummyData';
import HorizontalControl from './HorizontalControl';

import HoverBox from './HoverBox';
import MeasurementBox from './MeasurementBox';

import Plot from './Plot.js';

let dummyData = getDummyData();

async function serial() {
  const port = await navigator.serial.requestPort();
  console.log(port);

  await port.open({baudRate: 9600});

  const reader = port.readable.getReader();
  const writer = port.writable.getWriter();

  let buffer;

  buffer = new Uint8Array([0]);
  await writer.write(buffer);


  buffer = new ArrayBuffer(4);
  buffer = await read(reader, buffer);
  let uint = new Uint32Array(buffer);
  console.log("trigger:", uint, buffer);


  let len = 0;
  buffer = new Uint8Array(96 * 1024 * 2);
  buffer = await read(reader, buffer);
  console.log(buffer.byteLength);
  console.log(buffer);

}
function ab2str(buf) {
  return String.fromCharCode.apply(null, new Uint8Array(buf));
}
function str2ab(str) {
  var buf = new ArrayBuffer(str.length); // 2 bytes for each char
  var bufView = new Uint8Array(buf);
  for (var i=0, strLen=str.length; i < strLen; i++) {
  bufView[i] = str.charCodeAt(i);
  }
  return buf;
}

async function read2(reader, len) {
  let buffer = new Uint8Array(len);
  let rec = 0;
  while (rec < len) {
    const { value, done } = await reader.read(new ArrayBuffer(len));
    rec += value.byteLength;
  }
}


async function read(reader, buffer) {
  let offset = 0;
  while (offset < buffer.byteLength) {
    const { value, done } = await reader.read(
      new ArrayBuffer(buffer, offset)
    );
    console.log('read', buffer.byteLength);
    return buffer;
    //buffer = value.buffer;
    //offset += value.byteLength;
  }
  return buffer;
}




export default function App() {
  let [data, setData] = useState([]);
  let [test, setTest] = useState(0);
  let [smallData, setSmallData] = useState([]);
  let [USBDevice, setUSBDevice] = useState(null);
  let [captured, setCaptured] = useState(0);
  let [start, setStart] = useState(0);
  let [interval, setInterval2] = useState(0);
  let [running, setRunning] = useState(false);

  let [showPlot, setShowPlot] = useState(true);
  return (
    <div className="root">
      <div className="topbar">
      <span role="img" aria-label="dog">{USBDevice == null ? "❌" : "✅"} </span>

        <button onClick={serial}>serial</button>


        </div>
      <div className="main">
        <div className='plot'>
          {test}
        <WebglAppSin data={data} test={test}/>

        </div>
        <div className='side'>
          <ChannelControl number="1" color="#ffff0078" active="true"/>
          <ChannelControl number="2" color="#00800069" active="false"/>
          <ChannelControl number="3" color="#0000ff73" style={{opacity: 1}}/>
         <HorizontalControl/>

        </div>
      </div>
    </div>
  );
}
