import React, {useState} from 'react';
import './App.css';

import WebglAppSin from "./webglAppSin";

import ChannelControl from './ChannelControl';

import Button from 'react-bootstrap/Button';
import { ColorRGBA } from 'webgl-plot';

import getDummyData from './dummyData';

import ValueBox from './ValueBox';

let dummyData = getDummyData();


async function getData(USBDevice) {
  // Read (up to) 64 bytes of data on USB endpoint 2
 // let result = await USBDevice.isochronousTransferIn(2, Array(2).fill(1023));
  let result = await USBDevice.transferIn(2, 256);

  console.log(result);

  if (result.data == null) {
    console.log("received null");
    return null;
    }
  //console.log('received', result.data.byteLength, 'B');
  let newData = result.data;
  // Parse the received data
  let parsedData = [];
  for (let i = 0; i < result.data.byteLength; i++) {
    let val = newData.getUint8(i);
    parsedData.push(val);
  }
  return parsedData;
}




export default function App() {
  let [data, setData] = useState([[0]]);
  let [USBDevice, setUSBDevice] = useState(null);
  let [captured, setCaptured] = useState(0);



  async function connectDevice() {
    let device = await navigator.usb.requestDevice({ filters: [{ vendorId: 0x0 }] });
    await device.open();
    await device.claimInterface(0);
    setUSBDevice(device);
  }

  async function readData() {
      let newData = await getData(USBDevice);
      if (newData != null) {
        data.push(newData);
        setData(data);
        console.log('read', newData.length, 'datapoints');
        captured += newData.length;
        setCaptured(captured);
    }
  }

  function startCapture() {
    setInterval(readData, 1000);
  }

  function startSingle() {
    readData();
  }

  let [test, setTest] = useState(123);

  return (
    <div className="root">
      <div className="topbar">
      <span role="img" aria-label="dog">{USBDevice == null ? "❌" : "✅"} </span>

        <button onClick={connectDevice}>Request device</button>
        <button onClick={startCapture} disabled={USBDevice == null}>read data</button>
        <button onClick={startSingle} disabled={USBDevice == null}>read single</button>
        </div>
      <div className="main">
        <div className='plot'>
          <WebglAppSin data={dummyData}/>
        </div>
        <div className='side'>
          <ChannelControl/>


        </div>
      </div>
    </div>
  );
}
