import React, {useEffect, useState} from 'react';
import './App.css';

import WebglAppSin from "./webglAppSin";

import ChannelControl from './ChannelControl';

import Button from 'react-bootstrap/Button';
import { ColorRGBA } from 'webgl-plot';

import getDummyData from './dummyData';
import HorizontalControl from './HorizontalControl';

import HoverBox from './HoverBox';
import MeasurementBox from './MeasurementBox';

import Plot from './Plot.js';

let dummyData = getDummyData();


function ab2str(buf) {
  return String.fromCharCode.apply(null, new Uint8Array(buf));
}
function str2ab(str) {
  var buf = new ArrayBuffer(str.length); // 2 bytes for each char
  var bufView = new Uint8Array(buf);
  for (var i=0, strLen=str.length; i < strLen; i++) {
  bufView[i] = str.charCodeAt(i);
  }
  return buf;
}

async function readInto(reader, buffer) {
  let offset = 0;
  while (offset < buffer.byteLength) {
    const { value, done } = await reader.read(
      new Uint8Array(buffer, offset)
    );
    if (done) {
      break;
    }
    buffer = value.buffer;
    offset += value.byteLength;
  }
  return buffer;
}




export default function App() {
  let [data, setData] = useState([]);
  let [test, setTest] = useState(0);

  async function serial() {
    const port = await navigator.serial.requestPort();
    console.log(port);

    await port.open({baudRate: 9600});

    const reader = port.readable.getReader({mode: "byob"});
    const writer = port.writable.getWriter();

    let buffer;

    buffer = new ArrayBuffer([0]);
    writer.write(buffer);

    buffer = new ArrayBuffer(4);
    buffer = await readInto(reader, buffer);
    let trigger = new Uint32Array(buffer);
    console.log('trigger:', trigger[0]);

    buffer = new ArrayBuffer(96 * 2 * 1024);
    buffer = await readInto(reader, buffer);
    console.log(buffer);

    let parsedBuffer = new Uint8Array(buffer);
    let start = Array.from(parsedBuffer.slice(trigger));
    let end = Array.from(parsedBuffer.slice(0, trigger));
    //buffer = start.concat(end);
    buffer = Array.from()
    data = buffer;
    setData(buffer);

  }


  return (
    <div className="root">
      <div className="topbar">
      <span role="img" aria-label="dog">{true ? "❌" : "✅"} </span>

        <button onClick={serial}>serial</button>


        </div>
      <div className="main">
        <div className='plot'>
          {test}
        <WebglAppSin data={data} test={test}/>

        </div>
        <div className='side'>
          <ChannelControl number="1" color="#ffff0078" active="true"/>
          <ChannelControl number="2" color="#00800069" active="false"/>
          <ChannelControl number="3" color="#0000ff73" style={{opacity: 1}}/>
         <HorizontalControl/>

        </div>
      </div>
    </div>
  );
}
