import React, {useState} from 'react';

const style = {
  border: "1px solid black",
  padding: '1px',
  margin: '1px'
};

function ValueControl(props) {
  return (
    <fieldset>
      <legend>{}</legend>
{props.name}: <input value={props.value} size="2"/>{props.unit}
      <button>--</button><button>-</button><button>0</button><button>+</button><button>++</button>
    </fieldset>


  );
}

export default function ChannelControl(props) {
  return (
    <div style={style}>
      Channel {props.channel}
      <button>Active</button> <br/>
      <ValueControl name="Offset" value="150" unit="mV"/>
      <ValueControl name="Scale" value="200" unit="mV"/>
    </div>
  )
}
