import './ValueBox.css';

function formatData(data) {
    let negative = data < 0 ? true : false;
    if (negative) data *= -1;
   let digitsBefore = data.toString().split('.')[0].length;
    console.log(digitsBefore);

    let prefixes = ['', 'μ']

}

export default function ValueBox({name, unit, data, setData}) {
    formatData(data);

    return (
        <div className='ValueBox'>
            <div className='name'>{name}</div>
            <div className='content'>
                <div className='data'>{data} {unit}</div>
                <div className='buttons'>
                <button onClick={() => {setData(data - 10)}}>--</button>
                <button onClick={() => {setData(data - 1)}}>-</button>
                <button onClick={() => {setData(0)}}>Zero</button>
                <button onClick={() => {setData(data + 1)}}>+</button>
                <button onClick={() => {setData(data + 10)}}>++</button>
                </div>

            </div>       
        </div>
    );
}
